import React from "react";
import { useState, useEffect } from "react";

const Updateprofile = ({
  showModal,
  handleClose,
  profileImage,
  handleUpdateImage,
}) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);

  useEffect(() => {
    if (profileImage) {
      const base64Image = Buffer.from(profileImage, "binary").toString(
        "base64"
      );
      setImagePreview(`data:image/jpeg;base64,${base64Image}`);
    }
  }, [profileImage]);

  const handleFileRead = () => {
    const fileReader = new FileReader();

    // Check if the selected file is an image
    if (selectedFile) {
      console.log("Selected File Type:", selectedFile.type);

      const isImage =
        selectedFile.type.startsWith("image/") ||
        /\.(jpeg|jpg|png)$/i.test(selectedFile.name);

      if (isImage) {
        fileReader.onloadend = () => {
          setImagePreview(fileReader.result);
          handleFileChange(fileReader.result);
        };
        fileReader.readAsDataURL(selectedFile);
      } else {
        console.error("Selected file is not an image");
        // Handle the case where the selected file is not an image
      }
    }
  };

  const handleFileChange = (base64Image) => {
    // Pass the Base64-encoded image data to the parent component
    handleUpdateImage(base64Image);
  };

  return (
    <div
      className={`modal fade ${showModal ? "show" : ""}`}
      style={{ display: showModal ? "block" : "none" }}
      data-bs-theme="dark"
    >
      <div className="modal-dialog" role="document">
        <div className="modal-content text-white">
          <div className="modal-header">
            <h5 className="modal-title">Update Profile</h5>
            <button
              type="button"
              className="btn-close"
              onClick={handleClose}
            ></button>
          </div>
          <div className="modal-body text-center">
            <div className="position-relative">
              <label
                htmlFor="profileImageInput"
                className="profile-image-label"
              >
                <img
                  src={imagePreview || "https://placekitten.com/200/200"}
                  alt="Profile"
                  className="img-fluid rounded-circle profile-image"
                />
                <div className="overlay">
                  <i className="fa fa-upload fa-fw"></i>
                  <input
                    type="file"
                    id="profileImageInput"
                    className="visually-hidden"
                    onChange={(e) => {
                      setSelectedFile(e.target.files[0]);
                      handleFileRead(); // Call handleFileRead directly
                    }}
                  />
                </div>
              </label>
            </div>
          </div>
          <div className="modal-footer">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={handleClose}
            >
              Close
            </button>
            <button
              type="button"
              className="btn btn-primary"
              onClick={handleUpdateImage}
            >
              Save changes
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Updateprofile;
