import React, { useState } from "react";
import axios from "axios";

const Assignuser = ({ showModal, handleAssignTask, handleClose }) => {
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const fetchUserCompany = async () => {
    try {
      // Make a GET request to your backend API
      const response = await axios.get(
        "http://localhost:5000/api/getuserprofileadmincompany"
      );

      // Set the user profiles in the state
      setUserProfiles(response.data);
    } catch (error) {
      // Handle any errors that occurred during the fetch
      setError("Error fetching user profiles");
      console.error(error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // You can perform additional validation here if needed

    try {
      // Call the parent function to handle the assignment
      await handleAssignTask();

      // Reset the form fields and show success message

      setErrorMessage("");
      setSuccessMessage("Task assigned successfully!");
    } catch (error) {
      // Show error message if there's an issue with the assignment
      setSuccessMessage("");
      setErrorMessage("Error assigning task. Please try again.");
      console.error("Error assigning task:", error.message);
    }
  };
  const onClose = () => {
    handleClose();
  };

  return (
    <div
      className={`modal fade ${showModal ? "show" : ""}`}
      style={{ display: showModal ? "block" : "none" }}
      data-bs-theme="dark"
    >
      <div className="modal-dialog modal-dialog-centered">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title text-white">
              <i className="fa fa-user-plus fa-fw me-2"></i>Assign To
            </h5>
            <button
              type="button"
              className="btn-close"
              onClick={onClose}
            ></button>
          </div>
          <div className="modal-body text-white text-start">
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="formTaskTitle" className="form-label">
                  Users
                </label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Enter Task Title"
                />
              </div>

              {errorMessage && (
                <div className="alert alert-danger mt-3" role="alert">
                  {errorMessage}
                </div>
              )}
              {successMessage && (
                <div className="alert alert-success mt-3" role="alert">
                  {successMessage}
                </div>
              )}

              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={onClose}
                >
                  Close
                </button>
                <button type="submit" className="btn btn-primary">
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Assignuser;
