import React from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import Detailtask from "../task/Detailtask";
import { useState, useEffect, useContext } from "react";

const Archivetask = () => {
  const { loggedIn } = useContext(AuthContext);
  const [tasks, setTasks] = useState([]);
  const [showDetailTaskModal, setShowDetailTaskModal] = useState(false);
  const [selectDetailTask, setSelectDetailTask] = useState("");

  const getArchiveTask = async () => {
    try {
      const response = await axios.get(
        "http://localhost:5000/api/getarchivetask",
        {
          withCredentials: true,
        }
      );
      if (response.status === 200) {
        console.log("Archived Fetch Successfully", response.data);
        setTasks(response.data); // Set tasks directly from response data
      }
    } catch (error) {
      console.error(
        "Error fetching Archived tasks:",
        error.response.data.error
      );
    }
  };

  useEffect(() => {
    if (loggedIn) {
      getArchiveTask();
    }
  }, [loggedIn]);

  return (
    <div className="container-fluid mt-5 p-3 border">
      <h2>Requests for Approval</h2>
      <div className="border p-3">
        <table className="table table-dark table-striped table-hover table-bordered">
          <thead>
            <tr>
              <th>Task Title</th>
              <th>Task Detail</th>
              <th>Assigned Users</th>
              <th>Requesting User</th>
            </tr>
          </thead>
          <tbody>
            {requestList.map((request, index) => (
              <tr key={index}>
                <td>{request.TaskTitle}</td>
                <td>
                  <button
                    className="btn btn-outline-info"
                    type="button"
                    onClick={() => {
                      setShowDetailTaskModal(true);
                      setSelectDetailTask(request);
                    }}
                  >
                    <i className="fa fa-eye fa-fw " aria-hidden="true"></i> View
                  </button>
                </td>

                <td>
                  <button
                    className="btn btn-outline-info"
                    type="button"
                    onClick={() => {
                      setShowUserCardModal(true);
                      setSelectAssignedUser(JSON.parse(request.AssignedUserID));
                    }}
                  >
                    <i className="fa fa-eye fa-fw " aria-hidden="true"></i>{" "}
                    Detail
                  </button>
                </td>
                <td>
                  <button
                    className="btn btn-outline-success me-2"
                    type="button"
                    onClick={() => {
                      setShowUserCardSelectModal(true);
                      setSelectRequestUser(
                        JSON.parse(request.RequestJoinUserID)
                      );
                      setSelectTaskId(request.TaskID);
                    }}
                  >
                    <i className="fa fa-check fa-fw " aria-hidden="true"></i>{" "}
                    Approve
                  </button>
                  <button
                    className="btn btn-outline-danger"
                    type="button"
                    onClick={() => {
                      removeRequestJoinTask(request.TaskID);
                    }}
                  >
                    <i className="fa fa-remove fa-fw " aria-hidden="true"></i>{" "}
                    Remove
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <h2>Tasks Created by Users</h2>
      <div className="border p-3">
        <table className="table table-dark table-striped table-hover table-bordered">
          <thead>
            <tr>
              <th>Task Title</th>
              <th>Task Detail</th>
              <th>Task Priority</th>
              <th>Task Type</th>

              <th>ApprovalStatus</th>
              <th className="text-center">Action</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((task) => (
              <tr key={task.TaskID}>
                <td>{task.TaskTitle}</td>
                <td>
                  <button
                    className="btn btn-outline-info "
                    type="button"
                    onClick={() => {
                      setShowDetailTaskModal(true);
                      setSelectDetailTask(task);
                    }}
                  >
                    <i className="fa fa-eye fa-fw " aria-hidden="true"></i> View
                  </button>
                </td>
                <td>
                  {task.PriorityID === 1
                    ? "Low"
                    : task.PriorityID === 2
                    ? "Medium"
                    : "High"}
                </td>
                <td>
                  {task.TaskTypeID === 1
                    ? "Small Task"
                    : task.TaskTypeID === 2
                    ? "Medium Task"
                    : task.TaskTypeID === 3
                    ? "Large Task"
                    : "Unknown"}
                </td>

                <td>{task.ApprovalStatus}</td>

                <td className="text-center">
                  {/* Add action buttons as needed */}
                  <div className="dropdown" data-bs-theme="dark">
                    <button
                      className="btn btn-outline-primary dropdown-toggle"
                      type="button"
                      data-bs-toggle="dropdown"
                      aria-haspopup="true"
                      aria-expanded="false"
                    >
                      <i
                        className="fa fa-ellipsis-v fa-fw "
                        aria-hidden="true"
                      ></i>
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <Usercard
        showModal={showUserCardModal}
        user={selectAssignedUser}
        handleClose={() => {
          setShowUserCardModal(false);
          setSelectAssignedUser("");
        }}
      />

      <Detailtask
        showModal={showDetailTaskModal}
        selectDetailTask={selectDetailTask}
        handleClose={() => {
          setShowDetailTaskModal(false);
          setSelectDetailTask("");
        }}
      />
    </div>
  );
};

export default Archivetask;
