import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.min.js";
import "font-awesome/css/font-awesome.min.css";

const Assignuser = ({ showModal, handleAssignTask, onClose }) => {
  const [taskTitle, setTaskTitle] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    // You can perform additional validation here if needed

    try {
      // Call the parent function to handle the assignment
      await handleAssignTask();

      // Reset the form fields and show success message
      setTaskTitle("");
      setErrorMessage("");
      setSuccessMessage("Task assigned successfully!");
    } catch (error) {
      // Show error message if there's an issue with the assignment
      setSuccessMessage("");
      setErrorMessage("Error assigning task. Please try again.");
      console.error("Error assigning task:", error.message);
    }
  };

  return (
    <div
      className={`modal fade ${showModal ? "show" : ""}`}
      style={{ display: showModal ? "block" : "none" }}
      data-bs-theme="dark"
    >
      <div className="modal-dialog modal-dialog-centered">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title text-white">
              <i className="fa fa-task fa-fw me-2"></i>Add Task
            </h5>
            <button
              type="button"
              className="btn-close"
              onClick={onClose}
            ></button>
          </div>
          <div className="modal-body text-white text-start">
            <form onSubmit={handleAssignTask}>
              <div className="mb-3">
                <label htmlFor="formTaskDeadline" className="form-label">
                  Task Deadline
                </label>
                <input
                  type="date"
                  className="form-control"
                  placeholder="Enter Task Deadline"
                />
              </div>

              {errorMessage ? (
                <div className="alert alert-danger mt-3" role="alert">
                  {errorMessage}
                </div>
              ) : successMessage ? (
                <div className="alert alert-success mt-3" role="alert">
                  {successMessage}
                </div>
              ) : null}

              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={onClose}
                >
                  Close
                </button>
                <button type="submit" className="btn btn-primary">
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Assignuser;
