import React, { useState, useEffect } from "react";
import axios from "axios";
import { AuthContext } from "./AuthContext";
import { useContext } from "react";
import { Navigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "font-awesome/css/font-awesome.min.css";

const Usermgmt = () => {
  const { loggedIn, companyName } = useContext(AuthContext);
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [showCreateModal, setShowCreateModal] = useState(false);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(
          "http://localhost:5000/api/getuserdata",
          { withCredentials: true }
        );

        if (response.status === 200) {
          setUsers(response.data);
          console.log("Fetched user data:", response.data);
        }
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    if (loggedIn) {
      fetchUserData();
    }
  }, [loggedIn]);

  if (!loggedIn) {
    // If the user is not logged in, redirect to the login page
    return <Navigate to="/login" />;
  }

  const filteredUsers = users.filter((user) =>
    user.Username.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container-fluid mt-5 p-3 border">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <div>
          <center>
            <h1>
              <i className="fa fa-building" aria-hidden="true"></i>
              {companyName}
            </h1>
          </center>
        </div>
        <div className="d-flex align-items-center">
          <input
            type="text"
            placeholder="Search by Username"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="form-control me-2"
          />
          <button
            className="btn btn-primary text-nowrap"
            onClick={() => setShowCreateModal(true)}
          >
            <i className="fa fa-plus me-2" aria-hidden="true"></i>Add User
          </button>
        </div>
      </div>

      <div className="border p-3">
        <button
          className="btn btn-primary text-nowrap mb-3"
          onClick={() => setShowCreateModal(true)}
        >
          <i className="fa fa-plus me-2" aria-hidden="true"></i>Add User
        </button>

        <table className="table table-dark table-striped table-hover table-bordered">
          {/* ... (existing code) ... */}
        </table>
      </div>

      {/* Create User Modal */}
      <div
        className={`modal fade ${showCreateModal ? "show" : ""}`}
        id="createUserModal"
        tabIndex="-1"
        aria-labelledby="exampleModalLabel"
        aria-hidden={!showCreateModal}
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">
                Create User
              </h5>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
                onClick={() => setShowCreateModal(false)}
              ></button>
            </div>
            <div className="modal-body">
              {/* Pure HTML form for creating a user */}
              <form
                onSubmit={(e) => {
                  e.preventDefault();
                  // Handle form submission here
                  // You can use the same logic as before, just without the function
                  // Retrieve values directly from the form elements
                  const username = document.getElementById("username").value;
                  const firstName = document.getElementById("firstName").value;
                  const lastName = document.getElementById("lastName").value;
                  const email = document.getElementById("email").value;
                  // Perform the necessary actions (e.g., API call)
                  // ...
                  // Optionally, you can update the user list by refetching data
                  // fetchUserData();
                  // Close the modal
                  setShowCreateModal(false);
                }}
              >
                <div className="mb-3">
                  <label htmlFor="username" className="form-label">
                    Username
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="username"
                    name="username"
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="firstName" className="form-label">
                    First Name
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="firstName"
                    name="firstName"
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="lastName" className="form-label">
                    Last Name
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="lastName"
                    name="lastName"
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="email" className="form-label">
                    Email
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    id="email"
                    name="email"
                  />
                </div>
                <button type="submit" className="btn btn-primary">
                  Create
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Usermgmt;
