import React, { useState } from "react";

const Assignuser = ({ showModal, handleAssignTask, handleClose }) => {
  const [assignedUserIds, setAssignedUserIds] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleAssignClick = () => {
    // Validate assignedUserIds here if needed

    // Call the parent function to handle the assignment
    handleAssignTask();

    // Close the modal
    handleClose();
  };

  return (
    <div
      className={`modal fade ${showModal ? "show" : ""}`}
      tabIndex="-1"
      role="dialog"
      aria-labelledby="assignUserModal"
      aria-hidden={!showModal}
    >
      <div className="modal-dialog" role="document">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="assignUserModalLabel">
              Assign Users to Task
            </h5>
            <button
              type="button"
              className="btn-close"
              data-bs-dismiss="modal"
              aria-label="Close"
              onClick={handleClose}
            ></button>
          </div>
          <div className="modal-body">
            <div className="mb-3">
              <label htmlFor="assignedUserIds" className="form-label">
                Assigned User IDs (comma-separated):
              </label>
              <input
                type="text"
                className="form-control"
                id="assignedUserIds"
                value={assignedUserIds}
                onChange={(e) => setAssignedUserIds(e.target.value)}
              />
            </div>
            {errorMessage && (
              <div className="alert alert-danger" role="alert">
                {errorMessage}
              </div>
            )}
          </div>
          <div className="modal-footer">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={handleClose}
            >
              Close
            </button>
            <button
              type="button"
              className="btn btn-primary"
              onClick={handleAssignClick}
            >
              Assign
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Assignuser;
