// Usermgmt.js

import React, { useState, useEffect } from "react";
import axios from "axios";
import { AuthContext } from "./AuthContext";
import { useContext } from "react";

import { Navigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

const Usermgmt = () => {
  const { loggedIn, companyName } = useContext(AuthContext);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(
          "http://localhost:5000/api/getuserdata",
          { withCredentials: true }
        );

        if (response.status === 200) {
          setUsers(response.data);
          console.log("Fetched user data:", response.data);
        }
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    if (loggedIn) {
      fetchUserData();
    }
  }, [loggedIn]);

  if (!loggedIn) {
    // If the user is not logged in, redirect to the login page
    return <Navigate to="/login" />;
  }

  const handleAddRow = () => {
    const newUserId = Math.max(...userData.map((user) => user.UserID)) + 1;
    const newRow = {
      UserID: newUserId,
      // Initialize other properties as needed
    };

    setUserData([...userData, newRow]);
  };

  const handleDeleteRow = (userId) => {
    const updatedData = userData.filter((user) => user.UserID !== userId);
    setUserData(updatedData);
  };

  return (
    <div>
      <center>
        <h1>{companyName}</h1>
      </center>
      <table className="table table-dark table-striped table-hover table-bordered">
        <thead>
          <tr>
            <th>User ID</th>
            {/* Add more table headers as needed */}
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {userData.map((user) => (
            <tr key={user.UserID}>
              <td>{user.UserID}</td>
              {/* Add more table data cells as needed */}
              <td>
                <button
                  type="button"
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDeleteRow(user.UserID)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="text-center">
        <button
          type="button"
          className="btn btn-primary"
          onClick={handleAddRow}
        >
          Add Row
        </button>
      </div>
      {/* Add additional features or components related to user management */}
    </div>
  );
};

export default Usermgmt;
