// AuthContext.js

import React, { createContext, useState, useCallback } from "react";
import axios from "axios";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [username, setUsername] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);
  const [userid, setUserId] = useState("");
  const [userroletype, setUserRoleType] = useState("");
  const [companyName, setCompanyName] = useState("");
  const [UserRoleId, setUserRoleId] = useState("");

  const handleLogin = (
    loggedInUsername,
    LoggedInUserId,
    loggedInUserRoleType,
    LoggedInCompanyName,
    LoggedInUserRoleId
  ) => {
    setUsername(loggedInUsername);
    setUserId(LoggedInUserId);
    setUserRoleType(loggedInUserRoleType);
    setLoggedIn(true);
    setCompanyName(LoggedInCompanyName);
    setUserRoleId(LoggedInUserRoleId);
  };

  const handleLogout = async () => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/logout",
        {},
        { withCredentials: true }
      );

      if (response.data.success) {
        setUsername("");
        setUserId("");
        setUserRoleType("");
        setLoggedIn(false);
        setCompanyName("");
        setUserRoleId("");
      }
    } catch (error) {
      console.error("Error logging out:", error);
    }
  };

  const checkLoginStatus = useCallback(async () => {
    console.log("Running checkLoginStatus");
    try {
      const response = await axios.get(
        "http://localhost:5000/api/check-login",
        { withCredentials: true }
      );
      const { loggedIn, user } = response.data;

      if (loggedIn) {
        handleLogin(
          user.username,
          user.UserRoleId,
          user.RoleType,
          user.CompanyName,
          user.UserRoleId
        );
      } else {
        handleLogout();
      }
    } catch (error) {
      console.error(
        "Error checking login status:",
        error.response.data.message
      );
    }
  }, [handleLogin, handleLogout]);

  return (
    <AuthContext.Provider
      value={{
        username,
        loggedIn,
        userid,
        userroletype,
        companyName,
        UserRoleId,
        handleLogin,
        handleLogout,
        checkLoginStatus, // Make sure to include checkLoginStatus in the context
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
