import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Task = ({ loggedIn }) => {
  const navigate = useNavigate();

  useEffect(() => {
    // Use the effect to navigate when the component mounts or when loggedIn changes
    if (!loggedIn) {
      // If the user is not logged in, navigate to the login page
      navigate("/login");
    }
  }, [loggedIn, navigate]);

  if (!loggedIn) {
    // You can also return null or a loading indicator while navigating
    return null;
  }

  return <div>Task</div>;
};

export default Task;
