import React from "react";
import { useContext, useEffect, useState } from "react";
import defaultCompanyLogo from "./defaultlogo.png";
import { CompanyProvider } from "../context/CompanyContext";

const Company = () => {
  const { getCompanyLogo, companyLogo } = useContext(CompanyProvider);
  const [companyLogoPreview, setCompanyLogoPreview] = useEffect("");
  const [selectedFile, setSelectedFile] = useState();

  useEffect(() => {
    setCompanyLogoPreview(companyLogo);
  }, [companyLogo]);

  function handleChange(e) {
    console.log(e.target.files);
    const file = e.target.files[0];

    if (file) {
      setSelectedFile(file);

      // Create a Blob from the ArrayBuffer
      const blob = new Blob([file], { type: file.type });

      // Use createObjectURL with the Blob
      setCompanyLogoPreview(URL.createObjectURL(blob));
    }
  }

  return (
    <div className="card position-relative">
      <div className="card-body">
        <div className="text-center position-relative">
          <label htmlFor="profileImageInput" className="profile-image-label">
            <img
              src={companyLogoPreview || defaultCompanyLogo}
              alt="Profile"
              className="img-fluid rounded-circle profile-image mb-3"
              style={{
                width: "200px",
                height: "200px",
                border: "2px solid #000",
              }}
            />

            <div className="overlay">
              <i className="fa fa-upload fa-fw me-2"></i>Upload
              <input
                type="file"
                id="profileImageInput"
                className="visually-hidden"
                onChange={handleChange}
              />
            </div>
          </label>
        </div>
      </div>
    </div>
  );
};

export default Company;
