// App.js
import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./Login";
import Register from "./Register";
import Home from "./Home";
import axios from "axios";

const App = () => {
  const [username, setUsername] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);
  const [companyName, setCompanyName] = useState("");
  
  const handleLogin = async (loggedInUsername, navigate) => { // Added navigate as a parameter
    console.log('handleLogin:', loggedInUsername);
    setUsername(loggedInUsername);
    setLoggedIn(true);

    try {
      // Use the new /api/user-data endpoint to get additional user data
      const response = await axios.get("http://localhost:5000/api/user-data", { withCredentials: true });
      const { companyName } = response.data;

      setCompanyName(companyName);
    } catch (error) {
      console.error("Error getting user data:", error.response.data.message);
    }

    // Redirect to home
    navigate("/home"); // Changed from Navigate to navigate
  };

  const handleLogout = () => {
    axios
      .post("http://localhost:5000/api/logout", {}, { withCredentials: true })
      .then((response) => {
        if (response.data.success) {
          setLoggedIn(false);
          setUsername("");
          setCompanyName("");
        }
      })
      .catch((error) => {
        console.error("Error logging out:", error);
      });
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            loggedIn ? <Navigate to="/home" /> : <Login onLogin={handleLogin} />
          }
        />
        <Route
          path="/login"
          element={
            loggedIn ? <Navigate to="/home" /> : <Login onLogin={handleLogin} />
          }
        />
        <Route path="/register" element={<Register />} />
        <Route
          path="/home"
          element={
            loggedIn ? (
              <Home
                username={username}
                loggedIn={loggedIn}
                companyName={companyName}
                onLogout={handleLogout}
              />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
      </Routes>
    </Router>
  );
};

export default App;
