import React, { useState } from "react";
import axios from "axios";
import { useContext } from "react";
import { Navigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.min.js";
import "font-awesome/css/font-awesome.min.css";

const Task = () => {
  const { loggedIn } = useContext(AuthContext);

  const [taskData, setTaskData] = useState({
    TaskTitle: "",
    TaskDescription: "",
    TaskDeadline: "",
    PriorityID: 1,
    TaskTypeID: 1,
    DependentTaskID: null,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTaskData({
      ...taskData,
      [name]: value,
    });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        "http://localhost:5000/api/addtask",
        taskData,
        {
          withCredentials: true, // Include credentials in the request
        }
      );

      console.log("Task added successfully with ID:", response.data.taskId);

      // Optionally, you can redirect the user to a task details page or update the UI
    } catch (error) {
      console.error("Error adding task:", error.response.data.error);
      // Handle error, show a message to the user, etc.
    }
  };

  if (!loggedIn) {
    // If the user is not logged in, redirect to the login page
    return <Navigate to="/login" />;
  }

  return (
    <div className="container mt-4">
      <h2>Add Task</h2>
      <form onSubmit={handleFormSubmit}>
        <div className="mb-3">
          <label htmlFor="TaskTitle" className="form-label">
            Task Title:
          </label>
          <input
            type="text"
            className="form-control"
            id="TaskTitle"
            name="TaskTitle"
            value={taskData.TaskTitle}
            onChange={handleInputChange}
            required
          />
        </div>

        {/* Add similar input fields for other task details */}

        <div className="mb-3">
          <label htmlFor="PriorityID" className="form-label">
            Priority:
          </label>
          <select
            className="form-select"
            id="PriorityID"
            name="PriorityID"
            value={taskData.PriorityID}
            onChange={handleInputChange}
          >
            <option value="1">Low</option>
            <option value="2">Medium</option>
            <option value="3">High</option>
          </select>
        </div>

        <div className="mb-3">
          <label htmlFor="TaskTypeID" className="form-label">
            Task Type:
          </label>
          <select
            className="form-select"
            id="TaskTypeID"
            name="TaskTypeID"
            value={taskData.TaskTypeID}
            onChange={handleInputChange}
          >
            <option value="1">Type 1</option>
            <option value="2">Type 2</option>
            <option value="3">Type 3</option>
          </select>
        </div>

        <button type="submit" className="btn btn-primary">
          Add Task
        </button>
      </form>
    </div>
  );
};

export default Task;
