import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import Navbar from "./Navbar";
import Login from "./Login";
import Register from "./Register";
import Home from "./Home";
import Task from "./Task";
import Approval from "./Approval";
import Usermgmt from "./Usermgmt";
import Nopage from "./Nopage";
import { useAuth } from "./AuthContext"; // Import the useAuth hook

const App = () => {
  const { user, login, logout } = useAuth(); // Use the useAuth hook

  return (
    <Router>
      {user && (
        <Navbar
          isLoggedIn={user !== null}
          onLogout={logout}
          UserRoleId={user.UserRoleId}
        />
      )}
      <Routes>
        <Route path="/register" element={<Register />} />
        <Route
          path="/"
          element={
            user ? <Navigate to="/home" /> : <Login /> // Use the user object to check if logged in
          }
        />
        <Route
          path="/login"
          element={
            user ? <Navigate to="/home" /> : <Login /> // Use the user object to check if logged in
          }
        />
        <Route
          path="/home"
          element={
            user ? (
              <Home
                username={user.username}
                loggedIn={user !== null}
                companyName={user.companyName}
                onLogout={logout}
                UserRoleId={user.UserRoleId}
                userid={user.userid}
                userroletype={user.userroletype}
              />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/task"
          element={<Task loggedIn={user !== null} onLogout={logout} />}
        />
        <Route
          path="/usermgmt"
          element={
            <Usermgmt
              loggedIn={user !== null}
              UserRoleId={user.UserRoleId}
              companyName={user.companyName}
              onLogout={logout}
            />
          }
        />
        <Route
          path="/Approval"
          element={<Approval loggedIn={user !== null} onLogout={logout} />}
        />

        <Route path="*" element={<Nopage />} />
      </Routes>
    </Router>
  );
};

export default App;
