import React from "react";
import axios from "axios";
import { useContext } from "react";
import { Navigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.min.js";
import "font-awesome/css/font-awesome.min.css";

const Task = () => {
  const { loggedIn, userid, UserRoleId, companyId } = useContext(AuthContext);
  const [taskData, setTaskData] = useState({
    TaskTitle: "",
    TaskDescription: "",
    TaskDeadline: "",
    PriorityID: 1,
    CompanyID: companyId, // Use companyId from AuthContext
    TaskTypeID: 1,
    UserID: userid, // Use userid from AuthContext
    UserRoleID: UserRoleId, // Use UserRoleId from AuthContext
    Status: "Pending",
    DependentTaskID: null,
  });

  if (!loggedIn) {
    // If the user is not logged in, redirect to the login page
    return <Navigate to="/login" />;
  }

  return <div>Task</div>;
};

export default Task;
