// Home.js
import React from "react";
import { Navigate, Link } from "react-router-dom";

import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

const Home = ({ username, loggedIn, onLogout }) => {
  if (!loggedIn) {
    // If the user is not logged in, redirect to the login page
    return <Navigate to="/login" />;
  }

  return (
    <div className="d-flex" id="wrapper">
      {/* Sidebar */}
      <div className="bg-light border-right" id="sidebar-wrapper">
        <div className="sidebar-heading">Task Mgmt</div>
        <div className="list-group list-group-flush">
          <Link to="/home" className="list-group-item list-group-item-action">
            Home
          </Link>
          {/* Add more navigation links as needed */}
        </div>
      </div>

      {/* Page Content */}
      <div id="page-content-wrapper" style={{ width: "100%" }}>
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <button
            className="btn btn-primary"
            id="menu-toggle"
            onClick={() => {
              document.getElementById("wrapper").classList.toggle("toggled");
            }}
          >
            Toggle Menu
          </button>

          <div className="ml-auto">
            <ul className="navbar-nav">
              <li className="nav-item">
                <p className="navbar-text mr-3">Welcome, {username}!</p>
              </li>
              <li className="nav-item">
                <button className="btn btn-danger" onClick={onLogout}>
                  Logout
                </button>
              </li>
            </ul>
          </div>
        </nav>

        {/* Main Content */}
        <div className="container mt-5">
          <div className="row justify-content-center">
            <div className="col-md-6">
              <div className="card">
                <div className="card-header bg-primary text-white">
                  Welcome, {username}!
                </div>
                <div className="card-body">
                  <p className="card-text">
                    Hello, {username}! Welcome back to the home page.
                  </p>
                  {/* You can add the content of your home page here */}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
