import React from "react";

const Updateprofile = ({ showModal, handleClose, handleUpdateImage }) => {
  return (
    <div
      className={`modal fade ${showModal ? "show" : ""}`}
      style={{ display: showModal ? "block" : "none" }}
      data-bs-theme="dark"
    >
      <div className="modal-dialog" role="document">
        <div className="modal-content text-white">
          <div className="modal-header">
            <h5 className="modal-title">Update Profile Image</h5>
            <button
              type="button"
              className="btn-close"
              onClick={handleClose}
            ></button>
          </div>
          <div className="modal-body">
            {/* Add form elements to allow users to upload a new image */}
            {/* Example: <input type="file" onChange={(e) => handleFileChange(e)} /> */}
          </div>
          <div className="modal-footer">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={handleClose}
            >
              Close
            </button>
            <button
              type="button"
              className="btn btn-primary"
              onClick={handleUpdateImage}
            >
              Save changes
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Updateprofile;
