import React from "react";
import { Navigate } from "react-router-dom";
import { AuthContext } from "./AuthContext";
import { useContext } from "react";

const Profile = () => {
  const {
    username,
    firstname,
    lastname,
    loggedIn,
    email,
    userroletype,
    companyName,
    UserRoleId,
  } = useContext(AuthContext);

  if (!loggedIn) {
    // If the user is not logged in, redirect to the login page
    return <Navigate to="/login" />;
  }

  const profileImageUrl = "https://placekitten.com/200/200"; // Replace with your actual profile image URL

  return (
    <div className="container mt-5">
      <h2>Profile Page</h2>
      <div className="card position-relative">
        <div className="card-body">
          <div className="text-center position-relative">
            {/* Display Profile Image */}
            <img
              src={profileImageUrl}
              alt="Profile"
              className="img-fluid rounded-circle"
              style={{ width: "200px", height: "200px" }}
            />
            <button
              className="btn btn-primary position-absolute bottom-0 end-0 m-2"
              onClick={() => {
                // Handle the edit profile action (e.g., navigate to edit profile page)
                console.log("Edit profile clicked");
              }}
            >
              Edit Profile
            </button>
          </div>
          <h2 className="text-center mb-0">{username}</h2>
          <p className="lead text-center mb-0">
            {firstname} {lastname}
          </p>
          <p className="text-muted text-center">{userroletype}</p>
        </div>
        <ul className="list-group list-group-flush">
          <li className="list-group-item">
            <i className="fa fa-envelope me-2"></i>Email : {email}
          </li>
          <li className="list-group-item">
            <i className="fa fa-building me-2"></i>Company : {companyName}
          </li>
          <li className="list-group-item">
            <i className="fa fa-id-badge me-2 "></i>User Role :{" "}
            {UserRoleId === 2 ? "Admin" : "User"}
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Profile;
