import React, { useState } from "react";
import axios from "axios";
import { useContext } from "react";
import { Navigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import AddTask from "./Addtask";

import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.min.js";
import "font-awesome/css/font-awesome.min.css";

const Task = () => {
  const { loggedIn } = useContext(AuthContext);

  if (!loggedIn) {
    // If the user is not logged in, redirect to the login page
    return <Navigate to="/login" />;
  }

  return (
    <div className="container-fluid mt-5 p-3 border">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <div>
          <center>
            <h1>
              <img
                alt=""
                src={imageLogo || defaultCompanyLogo}
                className="img-fluid rounded-circle company-logo mb-2"
                style={{
                  width: "50px",
                  height: "50px",
                  border: "2px solid #000",
                  marginRight: "10px",
                }}
              />
              {companyName}
            </h1>
          </center>
        </div>
        <div className="d-flex align-items-center">
          <input
            type="text"
            placeholder="Search by Username"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="form-control me-2"
          />
          <button
            type="button"
            className="btn btn-primary text-nowrap"
            onClick={() => setShowUserModal(true)}
          >
            <i className="fa fa-plus me-2" aria-hidden="true"></i>Add
          </button>
          <UserModal
            showModal={showUserModal}
            handleClose={() => {
              setShowUserModal(false);
              setErrorMessage(""); // Clear error message
              setSuccessMessage("");
            }}
            handleCreateUser={handleCreateUser}
            successMessage={successMessage}
            errorMessage={errorMessage}
          />
        </div>
      </div>

      <div className="border p-3">
        <table className="table table-dark table-striped table-hover table-bordered">
          <thead>
            <tr>
              <th>User ID</th>
              <th>Username</th>

              <th>UserRole</th>
              <th>UserStatus</th>
              <th className="text-center">Action</th>
              {/* Add more fields as needed */}
            </tr>
          </thead>
          <tbody>
            {filteredUsers.map((user) => (
              <tr key={user.UserID}>
                <td>{user.UserID}</td>
                <td>{user.Username}</td>

                <td>{user.RoleType}</td>
                <td>{user.IsActive === 1 ? "Active" : "Inactive"}</td>
                <td className="text-center"></td>
                {/* Add more fields as needed */}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {/* Add additional features or components related to user management */}
    </div>
  );
};
export default Task;
