import React from "react";
import { Navigate } from "react-router-dom";
import { AuthContext } from "./AuthContext";
import { useContext, useState, useEffect } from "react";
import axios from "axios";
import Updateprofile from "./Updateprofile";

import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.min.js";
import "font-awesome/css/font-awesome.min.css";

const Profile = () => {
  const {
    username,
    firstname,
    lastname,
    loggedIn,
    email,
    userroletype,
    companyName,
    UserRoleId,
  } = useContext(AuthContext);

  const [showUpdateProfileModal, setShowUpdateProfileModal] = useState(false);
  const [profileImage, setProfileImage] = useState(null);

  const fetchUserProfile = async () => {
    try {
      const response = await axios.get(
        "http://localhost:5000/api/getuserprofile", // Update the URL based on your backend route
        { withCredentials: true }
      );

      if (response.status === 200) {
        // Assuming the response data includes ImageData
        const userProfileImage = response.data.ImageData;
        setProfileImage(userProfileImage);
        console.log("Fetched user profile image data:", userProfileImage);

        // Update your UI or perform other actions with the fetched data
      }
    } catch (error) {
      console.error("Error fetching user profile data:", error);
    }
  };

  async function updateUserProfile(req, res) {
    try {
      console.log(`Received ${req.method} request for ${req.url}`);
      const userId = req.session.user.UserID;
      const imageData = req.body.imageData; // Assuming imageData is provided as a BLOB
      console.log("Received image data:", imageData);

      db.run(
        "INSERT OR REPLACE INTO ImageProfile (UserID, ImageData) VALUES (?, ?)",
        [userId, imageData]
      );

      res.status(200).json({ message: "Image profile updated successfully" });
    } catch (error) {
      console.error("Error in UpdateUserProfile:", error.message);
      res.status(500).json({ error: "Internal Server Error" });
    }
  }

  useEffect(() => {
    if (loggedIn) {
      fetchUserProfile();
    }
  }, [loggedIn]);

  if (!loggedIn) {
    // If the user is not logged in, redirect to the login page
    return <Navigate to="/login" />;
  }

  const profileImageUrl = "https://placekitten.com/200/200"; // Replace with your actual profile image URL

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-between align-items-center mb-auto">
        <h2>Profile Page</h2>
        <button
          className="btn btn-primary"
          onClick={() => {
            setShowUpdateProfileModal(true);
          }}
        >
          <i className="fa fa-edit fa-fw me-1"></i>Update
        </button>
        <Updateprofile
          showModal={showUpdateProfileModal}
          profileImage={profileImage}
          updateUserProfile={updateUserProfile}
          handleClose={() => {
            setShowUpdateProfileModal(false);
          }}
        />
      </div>

      <div className="card position-relative">
        <div className="card-body">
          <div className="text-center position-relative">
            {/* Display Profile Image */}
            <img
              src={profileImageUrl}
              alt="Profile"
              className="img-fluid rounded-circle"
              style={{ width: "200px", height: "200px" }}
            />
          </div>
          <h2 className="text-center mb-0">{username}</h2>
          <p className="lead text-center mb-0">
            {firstname} {lastname}
          </p>
          <p className="text-muted text-center">{userroletype}</p>
        </div>
        <ul className="list-group list-group-flush">
          <li className="list-group-item">
            <i className="fa fa-envelope me-2"></i>Email : {email}
          </li>
          <li className="list-group-item">
            <i className="fa fa-building me-2"></i>Company : {companyName}
          </li>
          <li className="list-group-item">
            <i className="fa fa-id-badge me-2 "></i>User Role :{" "}
            {UserRoleId === 2 ? "Admin" : "User"}
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Profile;
