import React, { useEffect, useState } from "react";
import { Navigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { useContext } from "react";
import axios from "axios";

import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.min.js";
import "font-awesome/css/font-awesome.min.css";

const Approval = () => {
  const { loggedIn } = useContext(AuthContext);
  const [requestList, setRequestList] = useState([]);
  const [profiles, setProfiles] = useState([]);

  const arrayToBlobUrl = (array, mimeType) => {
    const blob = new Blob([Uint8Array.from(array)], { type: mimeType });
    return URL.createObjectURL(blob);
  };

  const getAllRequestJoin = async () => {
    try {
      const response = await axios.get(
        "http://localhost:5000/api/getallrequestjoin",
        {
          withCredentials: true,
        }
      );
      if (response.status === 200) {
        console.log(
          "Request Join Tasks Fetched Successfully",
          response.data.taskAssignments
        );
        setRequestList(response.data.taskAssignments);
      }
    } catch (error) {
      console.error("Error fetching tasks:", error.response.data.error);
    }
  };

  const getMultiProfileInfo = async (userIds) => {
    console.log(userIds);
    try {
      const response = await axios.post(
        "http://localhost:5000/api/getmultiprofileinfo",
        { userIds },
        {
          withCredentials: true,
        }
      );
      if (response.status === 200) {
        console.log(response.data);
        return response.data;
      }
    } catch (error) {
      console.error("Error fetching profile information:", error);
    }
  };

  useEffect(() => {
    if (loggedIn) {
      getAllRequestJoin();
    }
  }, [loggedIn]);

  useEffect(() => {
    // Fetch profile information for all assigned users
    const assignedUserIds = requestList
      .map((request) => request.AssignedUserID)
      .flat();
    const requestJoinUserIds = requestList
      .map((request) => request.RequestJoinUserID)
      .flat();

    const fetchProfiles = async () => {
      const assignedProfiles = await getMultiProfileInfo(assignedUserIds);
      const requestProfiles = await getMultiProfileInfo(requestJoinUserIds);

      setProfiles({ assignedProfiles, requestProfiles });
    };

    if (requestList.length > 0) {
      fetchProfiles();
    }
  }, [requestList]);

  if (!loggedIn) {
    // If the user is not logged in, redirect to the login page
    return <Navigate to="/login" />;
  }

  return (
    <div>
      <h2>Requests for Approval</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Assignment ID</th>
            <th>Task ID</th>
            <th>Assigned Users</th>
            <th>Requesting User</th>
            <th>Assigned Profile</th>
            <th>Request Profile</th>
          </tr>
        </thead>
        <tbody>
          {requestList.map((request, index) => (
            <tr key={index}>
              <td>{request.AssignmentID}</td>
              <td>{request.TaskID}</td>
              <td>{request.AssignedUserID}</td>
              <td>{request.RequestJoinUserID}</td>
              <td>
                {profiles.assignedProfiles &&
                  profiles.assignedProfiles[request.AssignedUserID]?.name}
              </td>
              <td>
                {profiles.requestProfiles &&
                  profiles.requestProfiles[request.RequestJoinUserID]?.name}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Approval;
