import React from "react";
import { Navigate, Link } from "react-router-dom";
import { AuthContext } from "./AuthContext";
import { useContext } from "react";
import { FontAwesomeIcon } from "../node_modules/@fortawesome/fontawesome-free";
import {
  faHome,
  faTasks,
  faCheckCircle,
  faUsers,
  faSignOutAlt,
} from "../node_modules/fontawesome-free";

import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

const Navbar = () => {
  const { loggedIn, UserRoleId, handleLogout } = useContext(AuthContext);
  if (!loggedIn) {
    // If the user is not logged in, redirect to the login page
    return <Navigate to="/login" />;
  }

  return (
    <div>
      <nav className="navbar navbar-expand-sm bg-dark navbar-dark">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/home">
            <FontAwesomeIcon icon={faHome} /> Home
          </Link>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#collapsibleNavbar"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse " id="collapsibleNavbar">
            <ul className="navbar-nav">
              <li className="nav-item">
                <Link className="nav-link" to="/task">
                  <FontAwesomeIcon icon={faTasks} /> Task
                </Link>
              </li>
              {String(UserRoleId) === "2" && (
                <li className="nav-item">
                  <Link className="nav-link" to="/approval">
                    <FontAwesomeIcon icon={faCheckCircle} /> Approval
                  </Link>
                </li>
              )}
              {String(UserRoleId) === "2" && (
                <li className="nav-item">
                  <Link className="nav-link" to="/usermgmt">
                    <FontAwesomeIcon icon={faUsers} /> User Mgmt
                  </Link>
                </li>
              )}
            </ul>
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <button
                  className="nav-link btn btn-outline-danger"
                  onClick={handleLogout}
                >
                  <FontAwesomeIcon icon={faSignOutAlt} /> Log Out
                </button>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
