// App.js
import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import Navbar from "./Navbar";
import Login from "./Login";
import Register from "./Register";
import Home from "./Home";
import Task from "./Task";
import Approval from "./Approval";
import Usermgmt from "./Usermgmt";
import Nopage from "./Nopage";
import { AuthProvider, useAuth } from "./AuthContext"; // Import AuthProvider and useAuth

const AuthenticatedRoute = ({ element }) => {
  const auth = useAuth();

  return auth.loggedIn ? element : <Navigate to="/login" />;
};

const App = () => {
  return (
    <Router>
      <AuthProvider>
        {(auth) =>
          auth.loggedIn && (
            <Navbar
              isLoggedIn={auth.loggedIn}
              onLogout={auth.handleLogout}
              UserRoleId={auth.UserRoleId}
            />
          )
        }
        <Routes>
          <Route path="/register" element={<Register />} />
          <Route
            path="/"
            element={<AuthenticatedRoute element={<Navigate to="/home" />} />}
          />
          <Route
            path="/login"
            element={<AuthenticatedRoute element={<Navigate to="/home" />} />}
          />
          <Route
            path="/home"
            element={
              <AuthenticatedRoute
                element={
                  <Home
                    username={auth.username}
                    loggedIn={auth.loggedIn}
                    companyName={auth.companyName}
                    onLogout={auth.handleLogout}
                    UserRoleId={auth.UserRoleId}
                    userid={auth.userid}
                    userroletype={auth.userroletype}
                  />
                }
              />
            }
          />
          <Route path="/task" element={<Task />} />
          <Route
            path="/usermgmt"
            element={<AuthenticatedRoute element={<Usermgmt />} />}
          />
          <Route
            path="/Approval"
            element={<AuthenticatedRoute element={<Approval />} />}
          />
          <Route path="*" element={<Nopage />} />
        </Routes>
      </AuthProvider>
    </Router>
  );
};

export default App;
